import React, { Component } from "react";

import './faqs.scss'

class Faqs extends Component {
  constructor(props) {
    super(props)

    this.state = {
      faq1: [
        {
          title: 'Apa itu Saveduit',
          content: 'Adalah Program Hadiah Suka-suka dari Bank Jateng, cukup dari smartphone.'
        },
        {
          title: 'Apa itu Akun Saveduit ?',
          content: 'Adalah akun yang dibuat untuk bisa memiliki akses order hadiah tanpa perlu datang ke bank. Akun tanpa akses perbankan.'
        },
        {
          title: 'Apa itu Lock Up ?',
          content: 'Adalah proses penguncian saldo pada masa tertentu sesuai pesanan reward yang dipilih oleh nasabah, proses penguncian hanya dapat dilakukan melalui ATM Bank Jateng atau ibanking.bankjateng.co.id.'
        },
        {
          title: 'Apa itu Top Up ?',
          content: 'Adalah proses penambahan saldo tabungan yang dilakukan dengan setoran tunai ataupun non-tunai, sehingga saldo di dalam rekening bertambah.'
        },
        {
          title: 'Apa itu Transaksi ?',
          content: 'Adalah proses penyelesaian yang harus dilakukan untuk bisa mendapatkan reward dengan cara memasukkan kode booking yang didapat pada ATM Bank Jateng atau ibanking.bankjateng.co.id.'
        },
        {
          title: 'Cara kerja Program Saveduit ?',
          content: 'Nasabah cukup melakukan top-up dan Lock-up tertentu dalam jangka waktu yang ditentukan agar bisa mendapatkan reward yang diinginkan.'
        },
        {
          title: 'Jika tidak memiliki Tabungan Bima apakah bisa mendapatkan reward ?',
          content: 'Tidak bisa, karena transaksi harus tetap dilakukan melalui rekening Tabungan Bima.'
        },
        {
          title: 'Jika tidak memiliki BPD Card apakah bisa mendapatkan reward ?',
          content: 'Tidak bisa, karena transaksi penyelesaian salah satunya bisa dilakukan melalui BPD Card.'
        },
        {
          title: 'Apakah saldo saya akan hilang setelah mengikuti program Saveduit?',
          content: 'Tidak, saldo anda akan tetap ada di rekening tetapi tidak dapat ditransaksikan hingga jangka waktu kunci saldo selesai. Anda hanya akan dikenakan biaya administrasi bulanan untuk Tabungan Bima senilai Rp 7500,- (tujuh ribu lima ratus) rupiah dan biaya layanan kartu BPD Card untuk Silver Rp 3.000,- (tiga ribu rupiah), Gold Rp 5.000,- (lima ribu rupiah), Platinum Rp 10.000,- (sepuluh ribu rupiah).'
        }
      ],
      active: null,
    }
  }

  _changeActive = (val) => {
    this.setState({ active1: this.state.active === val ? null : val })
  }

  render() {
    return (
      <div className="faqs-container">
        FAQs
      </div>
    );
  }
};

export default Faqs;
