import React, { Component } from 'react'

import Logo from '../assets/logo bkk.png'
import Google from '../assets/gplay.png'
import Apple from '../assets/aplay.png'

import Facebook from '../assets/Icons/fb.png'
import Instagram from '../assets/Icons/ig.png'
import Twitter from '../assets/Icons/twitter.png'
import LinkedIn from '../assets/Icons/linkedin.png'

class Footer extends Component {
   constructor(props) {
      super(props)

      this.state = {
         icon: [
            { id: 1, img: Facebook, link: 'http://facebook.com' },
            { id: 2, img: Instagram, link: 'http://instagram.com' },
            { id: 3, img: Twitter, link: 'http://twitter.com' },
            { id: 4, img: LinkedIn, link: 'http://linkedin.com' },
         ],
         width: undefined
      }
   }
   
   componentDidMount = () => {
      this.setState({ width: window.innerWidth })

      window.addEventListener('resize', this.func)
   }

   componentWillUnmount = () => {
      window.removeEventListener('resize', this.func)
   }
   
   func = () => {
      this.setState({ width : window.innerWidth }) 
   }

   Left = () => {
      const { width } = this.state

      return (
         <div className={ (width > 768 ? 'padding-b-5' : 'padding-b-4') + ' flex padding-t-4' }>
            <div className='font-2 bold'>
               Lorem ipsum dolor sit amet, consetetur
            </div>
            
            <div className=' font-6 margin-t-space'>
               Lorem ipsum dolor sit amet, consetetur sadipscing elitr, sed diam
            </div>

            <div className='row margin-v-space'>
               <a className='float-center' href='# '>
                  <img
                     style={{ objectFit: 'contain'}}
                     src={ Google }
                     alt=''
                  />
               </a>

               <div className='margin-h-space' />

               <a className='float-center' href='# '>
                  <img
                     src={ Apple }
                     alt=''
                  />
               </a>
            </div>
            
            <div className='font-5 bold margin-t-space'>
               Kantor Pusat
            </div>
            
            <div className='font-6'>
               Jln. Tanjung No.11 A
            </div>

            <div className='font-6'>
               (024) 8604 2929
            </div>
         </div>
      )
   }

   Right = () => {
      const { width } = this.state
      
      return (
         <div className={ width > 768 ? 'padding-b-5 self-end' : 'padding-v-4 flex row' }>
            <div className={ (width > 768 ? null : 'flex') + ' float-center' }>
               <img
                  className='row img-contain'
                  src={ Logo }
                  alt=''
               />
            </div>
            
            { width <= 768 &&
               <div className='margin-h-space' />
            }

            <div className={ width > 768 ? null : 'flex' }>
               <div className={ (width > 768 ? 'margin-v-space' : 'margin-b-space') + ' font-6 bold' }>
                  Ikuti Kami
               </div>

               <div className={ (width > 768 ? null : 'flex') + ' row float-center space-between' }>
                  { this.state.icon.map((o) => (
                     <a href={ o.link } key={ o.id }>
                        <img
                           className='img-contain'
                           src={ o.img }
                           alt=''
                        />
                     </a>
                  ))}
               </div>
            </div>
         </div>
      )
   }

   render() {
      const { width } = this.state
      const { Left, Right } = this

      return (
         <div className='padding-h-3-dm bg-theme-alt font-white row-m'>
            <Left
               width={ width }
            />
            
            <div className='margin-h-space' />

            <Right
               width={ width }
            />
         </div>
      )
   }
}

export default React.memo(Footer)
