import React, { Component } from 'react'
import '../scss/components/header.scss'

import Logo from '../assets/icon-header.png'

import { NavLink } from 'react-router-dom'

class Header extends Component {
   constructor(props) {
      super(props)

      this.state = {
         burger: false,
         menu: [
            { id: 1, text: 'Home', route: '/' },
            { id: 2, text: 'Produk', route: '/product' },
            { id: 3, text: 'Area', route: '/faq' },
            { id: 4, text: 'Manajemen', route: '/product' },
            { id: 5, text: 'Pengajuan Online', route: '/faq' }
         ]
      }
   }

   burgerLoad = () => {
      const $navbarBurgers = Array.prototype.slice.call(document.querySelectorAll('.navbar-burger'), 0)
    
      if($navbarBurgers.length > 0) {
         $navbarBurgers.forEach( el => {
            const target = el.dataset.target
            const $target = document.getElementById(target)
   
            el.classList.toggle('is-active')
            $target.classList.toggle('is-active')
         })
      }

      this.setState({ burger: !this.state.burger })
   }

   close = () => {
      const $navbarBurgers = Array.prototype.slice.call(document.querySelectorAll('.navbar-burger'), 0)

      if($navbarBurgers.length > 0) {
         $navbarBurgers.forEach( el => {
            const target = el.dataset.target
            const $target = document.getElementById(target)
   
            el.classList.remove('is-active')
            $target.classList.remove('is-active')
         })
      }

      this.setState({ burger: !this.state.burger })
   }

   render() {
      const { burger, menu } = this.state

      return (
         <nav className='padding-h-4-nm navbar is-fixed-top bg-theme'>
            <div className='padding-l-4-om navbar-brand'>
               <div className='navbar-item padding'>
                  <img
                     src={ Logo }
                     alt=''
                  />
               </div>

               <div
                  className='navbar-burger burger'
                  data-target='menuHeader'
                  onClick={() => this.burgerLoad()}
               >
                  <span className='font-white' />
                  <span className='font-white' />
                  <span className='font-white' />
               </div>
            </div>

            <div
               className='header-navbar-menu navbar-menu bg-theme'
               id='menuHeader'
            >
               <div className='navbar-end'>
                  { menu.map((o) => (
                     <NavLink
                        className='header-menu-child navbar-item padding'
                        activeClassName='bg-transparent font-theme-2 bold'
                        onClick={() => burger && this.close()}
                        exact={ o.text === 'Home' ? true : false } to={ o.route }
                        key={ o.id }
                     >
                        { o.text }
                     </NavLink>
                     ))
                  }
               </div>
            </div>
         </nav>
      )
   }
}

export default Header
